                "conditions": [
                    {
                        "condition_name": "适用对象",
                        "condition_value": "江苏聚庆旅行社有限公司",
                        "restrictions": []
                    }
                ]
            }
        ]
    }

    示例2 - 单价格条件（无条件限制）：
    {
        "price": 100,
        "unit": "人次",
        "conditions": [
            {
                "condition_name": "门市价",
                "condition_value": "门市价",
                "restrictions": []
            }
        ]
    }                    

